import React, { useContext } from "react";
import { Nav, Navbar, Form, FormControl, Button } from "react-bootstrap";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import LoginPage from "./LoginPage";
import UserIncidents from "./UserIncidents";
import NewIncident from "./newIncident";
import { AuthProvider, AuthContext } from "./AuthContext";

const CustomNavbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useContext(AuthContext);

  console.log(isAuthenticated);
  return (
    <>
      <AuthContext.Consumer>
        {(context) => {
          console.log(context[0]);
          return (
            <Navbar bg="dark" variant="dark">
              <Navbar.Brand href="/">Service Manager</Navbar.Brand>
              <Nav className="mr-auto">
                <Nav.Link href="/my-incidents">My Incidents</Nav.Link>
                <Nav.Link href="/my-incidents/new">New Ticket</Nav.Link>
              </Nav>
              {context.isAuthenticated && (
                <Nav.Link
                  href="/logout"
                  style={{ color: "#fff", fontSize: "0.9rem" }}
                >
                  Logout
                </Nav.Link>
              )}
            </Navbar>
          );
        }}
      </AuthContext.Consumer>

      <Router>
        <Switch>
          <Route
            path="/"
            exact
            render={() => (
              <AuthProvider>
                <LoginPage />
              </AuthProvider>
            )}
          ></Route>

          <Route
            path="/my-incidents"
            exact
            render={() => (
              <AuthProvider>
                <UserIncidents />
              </AuthProvider>
            )}
          ></Route>
          <Route
            path="/my-incidents/new"
            exact
            render={() => (
              <AuthProvider>
                <NewIncident />
              </AuthProvider>
            )}
          ></Route>
        </Switch>
      </Router>
    </>
  );
};

export default CustomNavbar;
