import React, { useContext } from "react";
import { AuthContext } from "./AuthContext";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useContext(AuthContext);

  console.log(isAuthenticated);
  return (
    <nav className="navbar navbar-light bg-light">
      <a className="navbar-brand mb-0 h1" href="/my-incidents">
        Service Manager
      </a>
      {/* <a href="/my-incidents">My Incidents</a>
      <a href="/my-incidents/new">New Incident</a> */}
      <Router>
        <Link to="/">Login</Link> {" --- "}
        <Link to="/my-incidents">My Incidents</Link> {" --- "}
        <Link to="/my-incidents/new">New incident</Link> {" --- "}
        <Switch>
          <Route
            path="/"
            exact
            render={() => (
              <AuthProvider>
                <LoginPage />
              </AuthProvider>
            )}
          ></Route>
        </Switch>
      </Router>
      {isAuthenticated && <button>Logout</button>}
    </nav>
  );
};

export default Navbar;
