import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import { AuthContext } from "./AuthContext";
import "./componentsStyles/Navbar.css";

const CustomNavbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useContext(AuthContext);
  const [userName, setUserName] = useState("");

  useEffect(() => {
    axios({
      method: "get",
      url: "http://localhost:5000/api/auth/user",
      data: localStorage.getItem("user-id"),
      headers: { "Content-Type": "application/json", "x-auth-token": token },
    })
      .then((res) => setUserName(res.data.name))
      .catch((err) => setError(...error, err.data));
  }, []);

  console.log(isAuthenticated);
  return (
    <nav>
      <ul>
        <li>
          <div className="brand-link">
            <NavLink to="/">
              <h4>Service Manager</h4>
            </NavLink>
          </div>
        </li>
        <div className="links">
          <li>
            <NavLink to="/my-incidents/new">New Ticket</NavLink>
          </li>
          <div className="username">{userName && <h6>{userName}</h6>}</div>
          <div className="logout">
            {isAuthenticated && (
              <li>
                <NavLink
                  to="/logout"
                  style={{ color: "#fff", fontSize: "0.9rem" }}
                >
                  Logout
                </NavLink>
              </li>
            )}
          </div>
        </div>
      </ul>
    </nav>
  );
};

export default CustomNavbar;
